import React, { useEffect, useRef, useState } from 'react';
import { useCart, useDispatch } from './ContextReducer';

export default function YourComponent(props) {
    let dispatch = useDispatch();
    const priceRef = useRef();
    let options = props.options || {}; // Handle if options are not passed
    let priceOptions = Object.keys(options);
    let foodItem = props.foodItems;

    const [qty, setQty] = useState(1);
    const [size, setSize] = useState("");

    const handleAddtocart = async () => {
        if (!foodItem || !foodItem._id) {
            console.error("Food item is not defined or doesn't have an _id.");
            return;
        }
        let finalPrice = qty * parseInt(options[size]);
        await dispatch({
            type: "ADD",
            id: foodItem._id,
            name: foodItem.name,
            price: finalPrice,
            qty: qty,
            size: size
        });
    };

    useEffect(() => {
        setSize(priceRef.current.value);
    }, []);

    return (
        <div>
            <div className="card mt-3" style={{ width: "18rem", maxHeight: "360px" }}>
                <img src={foodItem.img} className="card-img-top" alt="..." style={{ height: "180px", objectFit: "cover" }} />
                <div className="card-body">
                    <h5 className="card-title">{foodItem.name}</h5>
                    <div className='container w-100'>
                        <select className='m-2 h-100 bg-success rounded' onChange={(e) => setQty(e.target.value)}>
                            {Array.from(Array(6), (e, i) => {
                                return (
                                    <option key={i + 1} value={i + 1}>{i + 1}</option>
                                );
                            })}
                        </select>
                        <select className='m-2 h-100 bg-success rounded' ref={priceRef} onChange={(e) => setSize(e.target.value)}>
                            {priceOptions.map((data) => {
                                return <option key={data} value={data}>{data}</option>;
                            })}
                        </select>
                        <div className='d-inline'>
                            â‚¹{qty * parseInt(options[size])}/-
                        </div>
                        <hr />
                        <button className='btn btn-success justify-centre ms-2' onClick={handleAddtocart}>Add To Cart</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
