// src/screens/Menu.js

import React from 'react';
import { Link } from 'react-router-dom';

export default function Menu() {
    // Sample data for food items
    const foodItems = [
        { id: 1, name: 'Pizza Margherita', price: '₹299', description: 'Classic Italian pizza with fresh mozzarella and basil.' },
        { id: 2, name: 'Spaghetti Carbonara', price: '₹349', description: 'Pasta with creamy sauce, pancetta, and Parmesan cheese.' },
        { id: 3, name: 'Caesar Salad', price: '₹199', description: 'Crisp romaine lettuce with Caesar dressing and croutons.' },
        { id: 4, name: 'Tandoori Chicken', price: '₹399', description: 'Spiced and grilled chicken served with mint chutney.' },
        { id: 5, name: 'Paneer Tikka', price: '₹249', description: 'Marinated cottage cheese chunks cooked in a tandoor.' }
    ];

    return (
        <div className="container mt-5">
            <h2 className="mb-4 text-center">Our Menu</h2>
            <div className="row">
                {foodItems.map((item) => (
                    <div key={item.id} className="col-md-6 col-lg-4 mb-4">
                        <div className="card h-100">
                            <div className="card-body">
                                <h5 className="card-title">{item.name}</h5>
                                <p className="card-text">{item.description}</p>
                                <div className="d-flex justify-content-between align-items-center">
                                    <span className="text-success">{item.price}</span>
                                    <button className="btn btn-primary">Add to Cart</button>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <div className="text-center mt-4">
                <Link to="/" className="btn btn-secondary">Back to Home</Link>
            </div>
        </div>
    );
}
